
public class ExpressionValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x =5, y = 10,res;
res = x + y * 2;
System.out.println("Output of x+y*2 = "+ res);		
res = x - y + 2;		
System.out.println("Output of x-y+2 = "+ res);		
res = (x+y)*2;		
System.out.println("Output of (x+y)*2 = "+ res);		
res = y%x;		
System.out.println("Output of y%x = "+ res);
	}

}
